import React, { useState } from 'react';
import WeatherItem from './WeatherItem';


export default function Search2() {
    const [text, settext] = useState("");
    const [oncl, setoncl] = useState(false)

    let handlechange=(event)=>{
        settext(event.target.value);
    }

    const cl1=(text)=>{
        if(text!=="")
        {
            setoncl(true);
            settext(text);
        }
        else{
            setoncl(false);
        }
    }

    return (
        <>
            <div className='container'>
                 <form className="d-flex ip my-4">
        <input className="form-control me-2 " type="search" placeholder="Search" value={text} onChange={handlechange} aria-label="Search"/>

      </form>
      <button className="btn btn-outline-primary btn" type="submit" onClick={cl1(text)}>Search</button>
      {/* <button className="btn btn-outline-primary btn" type="submit" onClick={this.cl2}>Reset</button> */}
            </div>
            <WeatherItem name={text}/>
            </>
    )
}
