import React, { Component } from 'react'
import WeatherItem from './WeatherItem';

export class Search extends Component {
    constructor(){
        super();
        this.state={
            text:"",
        }
    }

    handlechange=(event)=>{
        this.setState({
            text:event.target.value,
        })
        console.log(this.state.text);
    }

    render() {
        return(
            <>
            <form className="d-flex container my-5">
        <input className="form-control me-2" type="search" placeholder="Search By City/Town" value={this.state.text}  aria-label="Search"/>
        <button className="btn btn-outline-success" type="submit" onClick={this.handlechange}>Search</button>
      </form>

      </>
        )
    }
}

export default Search
