import React, { Component } from 'react'
import WeatherItem from './WeatherItem';

export class Search extends Component {
    constructor(){
        super();
        this.state={
            text:"Chennai",
            stext:"Mumbai",
            oncl:false,
        }
    }

    handlechange=(event)=>{
        this.setState({
            text:event.target.value,
        })
    }

    stextchange=()=>{
        this.setState({
            stext:this.state.text,
        })
    }

    cl=()=>{
        this.setState({
            oncl:true,
        })
    }

    cl2=()=>{
        this.setState({
            oncl:false,
        })
    }

    render() {
        return(
            <>
            <form className="d-flex container my-5">
        <input className="form-control me-2" type="search" placeholder="Search By City/Town" value={this.state.text} onChange={this.handlechange} aria-label="Search"/>
        </form>
        <button type="button" className="btn btn-primary" onClick={this.cl}>Search</button>
      {
          this.state.oncl?<WeatherItem city={this.state.text}/>:null

      }

      </>
        )
    }
}

export default Search
