import React, { Component } from 'react'
import WeatherItem from './WeatherItem';

export class Search extends Component {
    constructor(){
        super();
        this.state={
            text:"",
            oncl:false,
        }
    }

    handlechange=(event)=>{
        this.setState({text:event.target.value});
    }

    cl1=()=>{
        if(this.state.text!=="")
        {
            this.setState({oncl:true,text:this.state.text})
        }
    }

    cl2=()=>{
        this.setState({oncl:false,text:""})
    }

    render() {
        return (
            <>
            <div className='container'>
                 <form className="d-flex ip my-4">
        <input className="form-control me-2 " type="search" placeholder="Search" value={this.state.text} onClick={this.handlechange} aria-label="Search"/>

      </form>
      <button className="btn btn-outline-primary btn" type="submit" onClick={this.cl1}>Search</button>
      {/* <button className="btn btn-outline-primary btn" type="submit" onClick={this.cl2}>Reset</button> */}
            </div>
            {this.state.oncl?<WeatherItem name={this.state.text}/>:null}
            </>
        )


    }
}

export default Search
