import React, { Component } from "react";

export class WeatherItem extends Component {
  constructor(){
      super();
      this.state={
          city:"",
          temp:0,
          description:"",
          pressure:0,
          wind_speed:0,
          wind_degree:0,
          humidity:0,
      }
  }  

  async componentDidMount(){
      let url=`https://api.openweathermap.org/data/2.5/weather?q=${this.props.name}&appid=a543ddce70e8d60185426a9ef1e9e72c`;
      console.log("hello");
      let data = await fetch(url);
      let pdata=await data.json();
      this.setState({
          city:pdata.name,
          temp:((pdata.main.temp)-273.15).toPrecision(2),
          description:pdata.weather[0].description,
      })

  }

  async componentDidUpdate(prev){
    if(prev.name!==this.props.name)
    {
    let url=`https://api.openweathermap.org/data/2.5/weather?q=${this.props.name}&appid=a543ddce70e8d60185426a9ef1e9e72c`;
      console.log("hello");
      let data = await fetch(url);
      let pdata=await data.json();

      this.setState({
          city:pdata.name,
          temp:((pdata.main.temp)-273.15).toPrecision(2),
          description:pdata.weather[0].description,
      })
    }
  }


  render() {

    return (
      <>
        <div className="container ct">
          <div
            className="card text-white bg-primary mb-3 cd1"

          >

            <div className="card-body">
              <h5 className="card-title">City</h5>
              <p className="card-text">
                {this.state.city}
              </p>
            </div>
          </div>

          <div
            className="card text-white bg-primary mb-3 cd2"

          >

            <div className="card-body">
              <h5 className="card-title">Temperature</h5>
              <p className="card-text">
                {this.state.temp} &#8451;
              </p>
            </div>
          </div>

          <div
            className="card text-white bg-primary mb-3 cd3"

          >

            <div className="card-body">
              <h5 className="card-title">Description</h5>
              <p className="card-text" id="desc">
                {this.state.description}
              </p>
            </div>
          </div>

          <div
            className="card text-white bg-primary mb-3 cd1"

          >

            <div className="card-body">
              <h5 className="card-title">City</h5>
              <p className="card-text">
                {this.state.pressure} hPa
              </p>
            </div>
          </div>

          <div
            className="card text-white bg-primary mb-3 cd1"

          >

            <div className="card-body">
              <h5 className="card-title">City</h5>
              <p className="card-text">
                {this.state.humidity} %
              </p>
            </div>
          </div>

          <div
            className="card text-white bg-primary mb-3 cd1"

          >

            <div className="card-body">
              <h5 className="card-title">City</h5>
              <p className="card-text">
                Wind:
              </p>
              <p className="card-text">
                {this.state.wind_speed} Metre/Sec
              </p>
              <p className="card-text">
                {this.state.wind_degree} Degrees
              </p>
            </div>
          </div>


        </div>
      </>
    );
  }
}

export default WeatherItem;
