import React, { Component } from "react";
import Search from "./Search";
import Spinner from "./Spinner";

export class WeatherItem extends Component {
    static defaultProps={
        city:"New Delhi",
    }

    constructor(){
        super();
        this.state={
            city:"",
            temp:0,
            description:"",
            loader:true,
        }
    }

    async componentDidMount(){
        let url=`https://api.openweathermap.org/data/2.5/weather?q=${this.props.city}&appid=7ad849064133f7930711fd3a2885a69b`
        let data=await fetch(url);
        let pdata=await data.json();
        console.log(pdata);
        this.setState({
            city:pdata.name,
            temp:((pdata.main.temp)-273.15).toFixed(2),
            description:pdata.weather[0].description,
            loader:false,
        })
    }

  render() {

    return (
        <>
        {this.state.loader && <Spinner/>}

      if(!this.state.loader)
      {
      <div className="container">
        <div className="card text-white bg-primary mb-3 text-center ct" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h2 className="card-title">City</h2>
            <div className="card-text">
                <h1>{this.state.city}</h1>

            </div>
          </div>
        </div>

        <div className="card text-white bg-primary mb-3 text-center ct my-5" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h2 className="card-title">Temperature</h2>
            <div className="card-text">
                <h1>{this.state.temp} &#8451;</h1>

            </div>
          </div>
        </div>

        <div className="card text-white bg-primary mb-3 text-center ct my-5" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h2 className="card-title">Description</h2>
            <div className="card-text">
                <h1 className="desc">{this.state.description}</h1>

            </div>
          </div>
        </div>

      </div>
  }
      </>
    );


}

}

export default WeatherItem;
