import React, { Component } from "react";
import Search from "./Search";

export class WeatherItem extends Component {
    constructor(){
        super();
        this.state={
            city:"",
            temp:0,
            description:0,
        }
    }

    async componentDidMount(){
        let url=`https://api.openweathermap.org/data/2.5/weather?q=London&appid=7ad849064133f7930711fd3a2885a69b`
        let data=await fetch(url);
        let pdata=await data.json();
        this.setState({
            city:pdata.name,
            temp:pdata.main.temp,
            description:pdata.weather.description,
        })
    }
  render() {
    return (
        <>

      <div className="container">
        <div className="card text-white bg-primary mb-3 text-center ct" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h5 className="card-title">City</h5>
            <p className="card-text">
              {this.city}
            </p>
          </div>
        </div>

        <div className="card text-white bg-primary mb-3 text-center ct my-5" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h5 className="card-title">Temperature</h5>
            <p className="card-text">
              {this.temp}
            </p>
          </div>
        </div>

        <div className="card text-white bg-primary mb-3 text-center ct my-5" >
          {/* <div className="card-header">Header</div> */}
          <div className="card-body">
            <h5 className="card-title">Description</h5>
            <p className="card-text">
              {this.description}
            </p>
          </div>
        </div>
      </div>
      </>
    );
  }
}

export default WeatherItem;
