import React, { Component } from 'react'
import Spinner from './Spinner';
import WeatherItem from './WeatherItem';

export class Weather extends Component {


    constructor(){
        super();
        this.state={
            text:"New Delhi",
            city:"New Delhi",
            temp:0,
            description:"",
            loader:true,
            oncl:false,
        }
    }

    async componentDidMount(){
        let url=`https://api.openweathermap.org/data/2.5/weather?q=${this.state.text}&appid=7ad849064133f7930711fd3a2885a69b`
        let data=await fetch(url);
        let pdata=await data.json();
        console.log(pdata);
        this.setState({
            city:pdata.name,
            temp:((pdata.main.temp)-273.15).toFixed(2),
            description:pdata.weather[0].description,
            loader:false,
        })
    }

    handlechange=(event)=>{
        this.setState({
            text:event.target.value,
        })

    }

    cl=()=>{
        if(this.state.text!==""){
        this.setState({
            oncl:true,

        })
        }
        else{
            this.setState({
                oncl:false,
            })
        }
    }


    cl2=()=>{
        this.setState({
            oncl:false,
            text:"",
        })
    }

    async onclevent(){
        this.componentDidMount();
    }

    render() {
        return (
            <div className='container'>
                <form className="d-flex container my-5">
        <input className="form-control me-2" type="search" placeholder="Search By City/Town" value={this.state.text} onChange={this.handlechange} aria-label="Search"/>
        </form>
        <div className="bt">
        <button type="button" className="btn btn-outline-success bt1 mx-4" onClick={this.onclevent}>Search</button>
        <button type="button" className="btn btn-primary bt2 mx-4" onClick={this.cl2}>Reset</button>
        </div>
        {this.state.loader && <Spinner/>}
        {!this.state.loader && <WeatherItem city={this.state.city} temp={this.state.temp} description={this.state.description}/>}
            </div>
        )
    }
}

export default Weather
