import React, { Component } from 'react'
import WeatherItem from './WeatherItem';

export class Search extends Component {
    constructor(){
        super();
        this.state={
            text:"Chennai",
            stext:"Mumbai",
        }
    }

    handlechange=(event)=>{
        this.setState({
            text:event.target.value,
        })
        console.log(this.state.text);
    }

    stextchange=()=>{
        this.setState({
            stext:this.state.text,
        })
    }

    render() {
        return(
            <>
            <form className="d-flex container my-5">
        <input className="form-control me-2" type="search" placeholder="Search By City/Town" value={this.state.text} onChange={this.handlechange} aria-label="Search"/>
        <button type="button" class="btn btn-primary" onClick={this.handlechange}>Search</button>
      </form>
      {console.log(this.state.text)}
      <WeatherItem city={this.state.text}/>
      {console.log(this.state.text)}
      </>
        )
    }
}

export default Search
